package controlstatement;

public class Calculator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char x='+';
		int a=10;
		int b=20;
		switch(x) {
		case'+': System.out.println(a+b);
		break;
		case'-':System.out.println(a-b);
		break;
		case'/':System.out.println(a/b);
		break;
		case'*':System.out.println(a*b);
		break;
		
		}
		}
}

