package controlstatement;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     final int a=20;
     final int b=30;
     final int c=50;
     switch(a) {
     case a:System.out.println("this is 20");
     break;
     case b:System.out.println("this is 30");
     break;
     case c:System.out.println("this is 50");
     break;
     }
	}

}
